mapper.select:
select
  go_id, create_time, modify_time, black_user_id, white_user_Id, go_context, room_id, is_end, end_time, last_user_id, go_result
from gobang.gb_go


        and go_id = #{goId}


        and create_time = #{createTime}


        and modify_time = #{modifyTime}


        and black_user_id = #{blackUserId}


        and white_user_Id = #{whiteUserId}


        and go_context = #{goContext}


        and room_id = #{I}


        and is_end = #{isEnd}


        and end_time = #{endTime}


        and last_user_id = #{lastUserId}


        and go_result = #{goResult}
