  go_id, create_time, modify_time, black_user_id, white_user_Id, go_context, is_end, end_time, last_user_id, go_result
from gobang.gb_go


        and go_id = #{t.goId}


        and create_time = #{t.createTime}


        and modify_time = #{t.modifyTime}


        and black_user_id = #{t.blackUserId}


        and white_user_Id = #{t.whiteUserId}


        and go_context = #{t.goContext}


        and is_end = #{t.isEnd}


        and end_time = #{t.endTime}


        and last_user_id = #{t.lastUserId}


        and go_result = #{t.goResult}


limit #{offset}, #{limit}

